import { useEffect, useState } from 'react';
import './Dashboard.css';


function Dashboard() {
  const server = "http://localhost:3001"
  const [students, setStudents] = useState([]);
  const [lastId, setLastId] = useState(NaN);
  function fetchStudents() {
    fetch(`${server}/api/students/search`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' }
    })
      .then(res => res.json())
      .then(data => setStudents(data.map(student => ({ ...student, checked: false }))))
      .catch(err => console.log(err));
  }
  useEffect(() => {
    fetchStudents();
    getLastId();

    const timer = setInterval(() => {
      fetchStudents();
      getLastId();
    }, 5000);

    return () => {
      clearInterval(timer);
    };
  }, []);
  function handleAddStudent(event) {
    const {id,value} = event.target
    setAddStudent(prevState=>({...prevState,[id]:value}))
  }
  function getLastId() {
    fetch(`${server}/api/students/search`, {method: 'POST', headers: {'Content-Type': 'application/json'},body: JSON.stringify({sort: {_id: -1},limit:1})})
  .then(res => res.json())
  .then(info => {
    setAddStudent(prevState=>({...prevState,_id:info[0]._id+1}))
    return setLastId(info[0]._id);}).catch(err => console.log(err))
  }
  function addStudentToDB(event) {
    var addbtn = document.getElementById("add-student")
    var body;
    fetch(`${server}/api/students/create`, {method: 'POST', headers: {'Content-Type': 'application/json'}, body: JSON.stringify(addStudent)})
    .then(res=>{
      let response=res;
      return response => res.json();})
    .then(body => {
      if (response.status === 200) {
        addbtn.textContent = body.message;
        setTimeout(() => {addbtn.textContent="Close Menu"}, 2000)
      } else {
        addbtn.style.backgroundColor = "red";
        addbtn.textContent = body.message;
        setTimeout(() => {addbtn.textContent="Close Menu";
      addbtn.style.backgroundColor="green"}, 2000)
  }}).catch(err => console.log(err))

};
useEffect(() => {getLastId()}, [])
fetchStudents()
const [addStudent, setAddStudent] = useState({show:false,name:null, _id: lastId+1, joining: null, gpa: null, section: null,program:null});
  return (
    <div id="dashboard-root">
      <h1>Student Dashboard</h1>
      <button id="add-student" onClick={() => {setAddStudent(prevState=>({...prevState,'show':!addStudent.show}));console.log(students);}}>{addStudent.show?"Close Menu":"Add Student"}</button>
      <button id="delete-students" onClick={() => {
        const checkedStudents = students.filter((student) => student.checked)
        console.log(checkedStudents)
        fetch(`${server}/api/students`, {method: 'DELETE', headers: {'Content-Type': 'application/json'}, body: JSON.stringify(checkedStudents.map((student) => student._id))})
        .then(res => res.json())
        .then(data => console.log(data))
        .catch(err => console.log(err))
      }}>Delete Checked Students</button>
      {addStudent.show ? (
      <div id="add-student">
      <table id='add-student'>
        <thead>
          <tr>
          <td><button id="addStudentToDB" className="editStudent" onClick={(event) => addStudentToDB(event)}><img src={require("../components/check.png")} alt="check add student" /></button></td>
          <td><input id="_id" placeholder="regNo" onChange={(event) => handleAddStudent(event)} value={String(lastId+1)}></input></td>
          <td><input id="name" placeholder="name" onChange={(event) => handleAddStudent(event)}></input></td>
          <td><input id="joining" placeholder="Joining" onChange={(event) => handleAddStudent(event)}></input></td>
          <td><input id="program" placeholder="Program" onChange={(event) => handleAddStudent(event)}></input></td>
          <td><input id="gpa" placeholder="gpa" onChange={(event) => handleAddStudent(event)}></input></td>
          <td><input id="section" placeholder="section" onChange={(event) => handleAddStudent(event)}></input></td>
          <td><button className="editStudent"><img src={require("../components/cancel.webp")} alt="cancel add student" /></button></td>
          </tr>
        </thead>
      </table>
      </div>
      ):<div id="add-student"></div>}
      <table id='main-table'>
        <thead>
          <tr>
          <td>Delete</td>
          <td>Reg No.</td>
          <td>Name</td>
          <td>Joining</td>
          <td>Program</td>
          <td>gpa</td>
          <td>Section</td>
          <td>Edit</td>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key = {student._id}>
              <td><input type='checkbox' onChange={(event) => {
                var tempStudents = [...students]
                tempStudents[students.findIndex((studentF) => studentF._id===student._id)].checked=event.target.checked
                setStudents(tempStudents)
              }}
                ></input></td>
              <td>{student._id}</td>
              <td>{student.name}</td>
              <td>{student.joining}</td>
              <td>{student.program}</td>
              <td>{student.gpa["$numberDecimal"]}</td>
              <td>{student.section}</td>
              <td><button className="editStudent"><img src={require("../components/settings.jpg")} alt="edit row" /></button></td></tr>))}
        </tbody>
      </table>
    </div>
  )}
export default Dashboard
