import './Login.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Login() {
  const navigate = useNavigate()
  const  [input, setInput] = useState({username: '', password: ''})

  const handleChange = (e) => {
    setInput(values => ({...values, [e.target.name]: e.target.value}))
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    console.log(input)
    var response = fetch('http://localhost:3001/api/login', {method: 'POST', body: JSON.stringify(input), headers: {'Content-Type': 'application/json'}})
    response.then(res => {
      if (res.ok) {
        navigate('/dashboard')}
      else {
        res.json().then(Json => console.log(Json))
        alert('Login failed');
      console.log(res)}
  })
  }

  return (
    <div id="login-root">
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <label >User</label>
        <input name='username' type="text" id="username" onChange={handleChange} />
        <label htmlFor="password">Password</label>
        <input name='password' type="password" id="password" onChange={handleChange}/>
        <button type="submit">Login</button>
      </form>
    </div>
  )
}

export default Login
