import './Login.css';
import { useState } from 'react';

function Login() {
  const  [input, setInput] = useState({user: '', password: ''})

  const handleChange = (e) => {
    setInput(values => ({...values, [e.target.name]: e.target.value}))
    console.log(input)
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    console.log(input)
    //HOW DO I MAKE A POST REQUEST HERE TO THE BACKEND?

    var response = fetch('http://localhost:3000/api/login', {method: 'POST', body: JSON.stringify(input), headers: {'Content-Type': 'application/json'}})
    console.log(response)
  }

  return (
    <div id="login-root">
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <label >User</label>
        <input name='user' type="text" id="user" onChange={handleChange} />
        <label htmlFor="password">Password</label>
        <input name='password' type="password" id="password" onChange={handleChange}/>
        <button type="submit">Login</button>
      </form>
    </div>
  )
}

export default Login
